  const handleSearch = (event) => {
    let value = event.target.value.toLowerCase();
    let result = [];
    console.log(value);
    result = allData.filter((data) => {
      return data.name.search(value) != -1;
    });
    setFilteredData(result);
  };

  useEffect(() => {
    getFilteredProducts()
      .then((response) => {
        console.log(response.data);
